mapper.select:
SELECT



FROM doctor  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.yisheng_name like CONCAT('%',#{params.yishengName},'%')


        and a.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')


        and a.yisheng_id_number like CONCAT('%',#{params.yishengIdNumber},'%')


        and a.zuozhen_shijian like CONCAT('%',#{params.zuozhenShijian},'%')


          and a.guahaojine >= #{params.guahaojineStart}   


          and a.guahaojine <= #{params.guahaojineEnd}   


          and a.guahaoshuliang >= #{params.guahaoshuliangStart}   


          and a.guahaoshuliang <= #{params.guahaoshuliangEnd}   


        and a.guahaoshuliang = #{params.guahaoshuliang}


        and a.sex_types = #{params.sexTypes}


        and a.keshi_types = #{params.keshiTypes}


        and a.zhiwei_types = #{params.zhiweiTypes}


        and a.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')


        and a.yisheng_content like CONCAT('%',#{params.yishengContent},'%')



order by a.${params.sort} ${params.order}
