mapper.select:
SELECT



FROM drug  a



        and a.id in

        #{item}



        and a.yaopin_name like CONCAT('%',#{params.yaopinName},'%')


        and a.yaopin_uuid_number like CONCAT('%',#{params.yaopinUuidNumber},'%')


        and a.shengchanchangjia like CONCAT('%',#{params.shengchanchangjia},'%')


        and a.yaopin_types = #{params.yaopinTypes}


          and a.yaopin_kucun_number >= #{params.yaopinKucunNumberStart}   


          and a.yaopin_kucun_number <= #{params.yaopinKucunNumberEnd}   


        and a.yaopin_kucun_number = #{params.yaopinKucunNumber}


          and a.yaopin_jinjia_money >= #{params.yaopinJinjiaMoneyStart}   


          and a.yaopin_jinjia_money <= #{params.yaopinJinjiaMoneyEnd}   


          and a.yaopin_new_money >= #{params.yaopinNewMoneyStart}   


          and a.yaopin_new_money <= #{params.yaopinNewMoneyEnd}   


        and a.yaopin_gongxiao_content like CONCAT('%',#{params.yaopinGongxiaoContent},'%')


        and a.yaopin_jinji_content like CONCAT('%',#{params.yaopinJinjiContent},'%')


        and a.yaopin_zhuyi_content like CONCAT('%',#{params.yaopinZhuyiContent},'%')


        and a.yaopin_content like CONCAT('%',#{params.yaopinContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
