mapper.select:
SELECT
,bingren.yonghu_id as bingrenYonghuId ,bingren.kanhuren_name as kanhurenName ,bingren.kanhuren_phone as kanhurenPhone ,bingren.bingren_name as bingrenName ,bingren.bingren_phone as bingrenPhone ,bingren.bingren_id_number as bingrenIdNumber ,bingren.sex_types as sexTypes ,bingren.bingren_types as bingrenTypes ,bingren.age as age ,bingren.bingren_photo as bingrenPhoto ,bingren.bingren_content as bingrenContent ,yaopin.yaopin_name as yaopinName ,yaopin.yaopin_uuid_number as yaopinUuidNumber ,yaopin.yaopin_photo as yaopinPhoto ,yaopin.shengchanchangjia as shengchanchangjia ,yaopin.yaopin_types as yaopinTypes ,yaopin.yaopin_kucun_number as yaopinKucunNumber ,yaopin.yaopin_jinjia_money as yaopinJinjiaMoney ,yaopin.yaopin_new_money as yaopinNewMoney ,yaopin.yaopin_gongxiao_content as yaopinGongxiaoContent ,yaopin.yaopin_jinji_content as yaopinJinjiContent ,yaopin.yaopin_zhuyi_content as yaopinZhuyiContent ,yaopin.yaopin_content as yaopinContent ,yisheng.yisheng_name as yishengName ,yisheng.yisheng_phone as yishengPhone ,yisheng.yisheng_id_number as yishengIdNumber ,yisheng.yisheng_photo as yishengPhoto ,yisheng.zuozhen_shijian as zuozhenShijian ,yisheng.guahaojine as guahaojine ,yisheng.guahaoshuliang as guahaoshuliang ,yisheng.keshi_types as keshiTypes ,yisheng.zhiwei_types as zhiweiTypes ,yisheng.yisheng_email as yishengEmail ,yisheng.yisheng_content as yishengContent
FROM drugs_utilization a left JOIN patient bingren ON a.bingren_id = bingren.id left JOIN drup yaopin ON a.yaopin_id = yaopin.id left JOIN doctor yisheng ON a.yisheng_id = yisheng.id
and a.id in
#{item}
and ( a.yaopin_id = #{params.yaopinId} )
and ( a.bingren_id = #{params.bingrenId} )
and ( a.yisheng_id = #{params.yishengId} )
and a.yaopinshiyong_uuid_number like CONCAT('%',#{params.yaopinshiyongUuidNumber},'%')
and a.yaopinshiyong_number >= #{params.yaopinshiyongNumberStart}
and a.yaopinshiyong_number <= #{params.yaopinshiyongNumberEnd}
and a.yaopinshiyong_number = #{params.yaopinshiyongNumber}
and UNIX_TIMESTAMP(a.caozuo_time) >= UNIX_TIMESTAMP(#{params.caozuoTimeStart})
and UNIX_TIMESTAMP(a.caozuo_time) <= UNIX_TIMESTAMP(#{params.caozuoTimeEnd})
and a.yaopinshiyong_content like CONCAT('%',#{params.yaopinshiyongContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.bingren_id IS NOT NULL
and bingren.yonghu_id = #{params.yonghuId}
and bingren.kanhuren_name like CONCAT('%',#{params.kanhurenName},'%')
and bingren.kanhuren_phone like CONCAT('%',#{params.kanhurenPhone},'%')
and bingren.bingren_name like CONCAT('%',#{params.bingrenName},'%')
and bingren.bingren_phone like CONCAT('%',#{params.bingrenPhone},'%')
and bingren.bingren_id_number like CONCAT('%',#{params.bingrenIdNumber},'%')
and bingren.sex_types = #{params.sexTypes}
and bingren.bingren_types = #{params.bingrenTypes}
and bingren.age >= #{params.ageStart}
and bingren.age <= #{params.ageEnd}
and bingren.age = #{params.age}
and bingren.bingren_content like CONCAT('%',#{params.bingrenContent},'%')
and UNIX_TIMESTAMP(bingren.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(bingren.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yaopin_id IS NOT NULL
and yaopin.yaopin_name like CONCAT('%',#{params.yaopinName},'%')
and yaopin.yaopin_uuid_number like CONCAT('%',#{params.yaopinUuidNumber},'%')
and yaopin.shengchanchangjia like CONCAT('%',#{params.shengchanchangjia},'%')
and yaopin.yaopin_types = #{params.yaopinTypes}
and yaopin.yaopin_kucun_number >= #{params.yaopinKucunNumberStart}
and yaopin.yaopin_kucun_number <= #{params.yaopinKucunNumberEnd}
and yaopin.yaopin_kucun_number = #{params.yaopinKucunNumber}
and yaopin.yaopin_jinjia_money >= #{params.yaopinJinjiaMoneyStart}
and yaopin.yaopin_jinjia_money <= #{params.yaopinJinjiaMoneyEnd}
and yaopin.yaopin_new_money >= #{params.yaopinNewMoneyStart}
and yaopin.yaopin_new_money <= #{params.yaopinNewMoneyEnd}
and yaopin.yaopin_gongxiao_content like CONCAT('%',#{params.yaopinGongxiaoContent},'%')
and yaopin.yaopin_jinji_content like CONCAT('%',#{params.yaopinJinjiContent},'%')
and yaopin.yaopin_zhuyi_content like CONCAT('%',#{params.yaopinZhuyiContent},'%')
and yaopin.yaopin_content like CONCAT('%',#{params.yaopinContent},'%')
and UNIX_TIMESTAMP(yaopin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(yaopin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yisheng_id IS NOT NULL
and yisheng.yisheng_name like CONCAT('%',#{params.yishengName},'%')
and yisheng.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')
and yisheng.yisheng_id_number like CONCAT('%',#{params.yishengIdNumber},'%')
and yisheng.zuozhen_shijian like CONCAT('%',#{params.zuozhenShijian},'%')
and yisheng.guahaojine >= #{params.guahaojineStart}
and yisheng.guahaojine <= #{params.guahaojineEnd}
and yisheng.guahaoshuliang >= #{params.guahaoshuliangStart}
and yisheng.guahaoshuliang <= #{params.guahaoshuliangEnd}
and yisheng.guahaoshuliang = #{params.guahaoshuliang}
and yisheng.keshi_types = #{params.keshiTypes}
and yisheng.zhiwei_types = #{params.zhiweiTypes}
and yisheng.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')
and yisheng.yisheng_content like CONCAT('%',#{params.yishengContent},'%')
order by a.${params.sort} ${params.order}
