mapper.select:
SELECT
,bingren.yonghu_id as bingrenYonghuId ,bingren.kanhuren_name as kanhurenName ,bingren.kanhuren_phone as kanhurenPhone ,bingren.bingren_name as bingrenName ,bingren.bingren_phone as bingrenPhone ,bingren.bingren_id_number as bingrenIdNumber ,bingren.sex_types as sexTypes ,bingren.bingren_types as bingrenTypes ,bingren.age as age ,bingren.bingren_photo as bingrenPhoto ,bingren.bingren_content as bingrenContent ,yisheng.yisheng_name as yishengName ,yisheng.yisheng_phone as yishengPhone ,yisheng.yisheng_id_number as yishengIdNumber ,yisheng.yisheng_photo as yishengPhoto ,yisheng.zuozhen_shijian as zuozhenShijian ,yisheng.guahaojine as guahaojine ,yisheng.guahaoshuliang as guahaoshuliang ,yisheng.keshi_types as keshiTypes ,yisheng.zhiwei_types as zhiweiTypes ,yisheng.yisheng_email as yishengEmail ,yisheng.yisheng_content as yishengContent
FROM `case` a left JOIN patient bingren ON a.bingren_id = bingren.id left JOIN doctor yisheng ON a.yisheng_id = yisheng.id
and a.id in
#{item}
and a.bingli_uuid_number like CONCAT('%',#{params.bingliUuidNumber},'%')
and ( a.bingren_id = #{params.bingrenId} )
and ( a.yisheng_id = #{params.yishengId} )
and UNIX_TIMESTAMP(a.jiancha_time) >= UNIX_TIMESTAMP(#{params.jianchaTimeStart})
and UNIX_TIMESTAMP(a.jiancha_time) <= UNIX_TIMESTAMP(#{params.jianchaTimeEnd})
and a.bingrenzishu_content like CONCAT('%',#{params.bingrenzishuContent},'%')
and a.jianchajieguo_content like CONCAT('%',#{params.jianchajieguoContent},'%')
and a.yishengkaifang_content like CONCAT('%',#{params.yishengkaifangContent},'%')
and a.yishengjianyi_content like CONCAT('%',#{params.yishengjianyiContent},'%')
and a.huafeijine >= #{params.huafeijineStart}
and a.huafeijine <= #{params.huafeijineEnd}
and UNIX_TIMESTAMP(a.xiacijiuzhen_time) >= UNIX_TIMESTAMP(#{params.xiacijiuzhenTimeStart})
and UNIX_TIMESTAMP(a.xiacijiuzhen_time) <= UNIX_TIMESTAMP(#{params.xiacijiuzhenTimeEnd})
and a.bingli_types = #{params.bingliTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.bingren_id IS NOT NULL
and bingren.yonghu_id = #{params.yonghuId}
and bingren.kanhuren_name like CONCAT('%',#{params.kanhurenName},'%')
and bingren.kanhuren_phone like CONCAT('%',#{params.kanhurenPhone},'%')
and bingren.bingren_name like CONCAT('%',#{params.bingrenName},'%')
and bingren.bingren_phone like CONCAT('%',#{params.bingrenPhone},'%')
and bingren.bingren_id_number like CONCAT('%',#{params.bingrenIdNumber},'%')
and bingren.sex_types = #{params.sexTypes}
and bingren.bingren_types = #{params.bingrenTypes}
and bingren.age >= #{params.ageStart}
and bingren.age <= #{params.ageEnd}
and bingren.age = #{params.age}
and bingren.bingren_content like CONCAT('%',#{params.bingrenContent},'%')
and UNIX_TIMESTAMP(bingren.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(bingren.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yisheng_id IS NOT NULL
and yisheng.yisheng_name like CONCAT('%',#{params.yishengName},'%')
and yisheng.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')
and yisheng.yisheng_id_number like CONCAT('%',#{params.yishengIdNumber},'%')
and yisheng.zuozhen_shijian like CONCAT('%',#{params.zuozhenShijian},'%')
and yisheng.guahaojine >= #{params.guahaojineStart}
and yisheng.guahaojine <= #{params.guahaojineEnd}
and yisheng.guahaoshuliang >= #{params.guahaoshuliangStart}
and yisheng.guahaoshuliang <= #{params.guahaoshuliangEnd}
and yisheng.guahaoshuliang = #{params.guahaoshuliang}
and yisheng.keshi_types = #{params.keshiTypes}
and yisheng.zhiwei_types = #{params.zhiweiTypes}
and yisheng.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')
and yisheng.yisheng_content like CONCAT('%',#{params.yishengContent},'%')
order by a.${params.sort} ${params.order}
