mapper.select:
SELECT
,yisheng.yisheng_name as yishengName ,yisheng.yisheng_phone as yishengPhone ,yisheng.yisheng_id_number as yishengIdNumber ,yisheng.yisheng_photo as yishengPhoto ,yisheng.zuozhen_shijian as zuozhenShijian ,yisheng.guahaojine as guahaojine ,yisheng.guahaoshuliang as guahaoshuliang ,yisheng.keshi_types as keshiTypes ,yisheng.zhiwei_types as zhiweiTypes ,yisheng.yisheng_email as yishengEmail ,yisheng.yisheng_content as yishengContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney
FROM doctor_chat a left JOIN doctor yisheng ON a.yisheng_id = yisheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and ( a.yisheng_id = #{params.yishengId} )
and a.yisheng_chat_issue_text like CONCAT('%',#{params.yishengChatIssueText},'%')
and UNIX_TIMESTAMP(a.issue_time) >= UNIX_TIMESTAMP(#{params.issueTimeStart})
and UNIX_TIMESTAMP(a.issue_time) <= UNIX_TIMESTAMP(#{params.issueTimeEnd})
and a.yisheng_chat_reply_text like CONCAT('%',#{params.yishengChatReplyText},'%')
and UNIX_TIMESTAMP(a.reply_time) >= UNIX_TIMESTAMP(#{params.replyTimeStart})
and UNIX_TIMESTAMP(a.reply_time) <= UNIX_TIMESTAMP(#{params.replyTimeEnd})
and a.zhuangtai_types = #{params.zhuangtaiTypes}
and a.yisheng_chat_types = #{params.yishengChatTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yisheng_id IS NOT NULL
and yisheng.yisheng_name like CONCAT('%',#{params.yishengName},'%')
and yisheng.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')
and yisheng.yisheng_id_number like CONCAT('%',#{params.yishengIdNumber},'%')
and yisheng.zuozhen_shijian like CONCAT('%',#{params.zuozhenShijian},'%')
and yisheng.guahaojine >= #{params.guahaojineStart}
and yisheng.guahaojine <= #{params.guahaojineEnd}
and yisheng.guahaoshuliang >= #{params.guahaoshuliangStart}
and yisheng.guahaoshuliang <= #{params.guahaoshuliangEnd}
and yisheng.guahaoshuliang = #{params.guahaoshuliang}
and yisheng.keshi_types = #{params.keshiTypes}
and yisheng.zhiwei_types = #{params.zhiweiTypes}
and yisheng.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')
and yisheng.yisheng_content like CONCAT('%',#{params.yishengContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
order by a.${params.sort} ${params.order}
